    private void findFactorial(int factorial)
    { 
         if(factorial<0)
        {
             txtFactorial.setText("");
             lblFactorialDisp.setText("");
             return;
        }
        
        if(factorial==0)
        {
             lblFactorialDisp.setText("1");
             return;
        }
        
        
        for(int i=factorial-1;i>0;i--)
        {
            factorial = factorial * i;
        }
        lblFactorialDisp.setText(String.valueOf(factorial));
    
    }
    
    private void btnFactorialActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnFactorialActionPerformed
        // TODO add your handling code here:
        int factorial;
        
        try
        {
            factorial = Integer.parseInt(txtFactorial.getText());
        }
        catch(Exception e)
        {   
            txtFactorial.setText("");
            lblFactorialDisp.setText("");
            return;
        }
        findFactorial(factorial);
       
        
        
    }//GEN-LAST:event_btnFactorialActionPerformed

    
     private void determinePrime(int num)
    {  
        
        
        int temp;
        boolean isPrime=true;
       
        if(num<=1)
        {
             isPrime = false;
        }
        else
        {
            for(int i=2;i<=num/2;i++)
            {
                temp = num % i;

                if(temp==0)
                {
                    isPrime = false;
                    break;
                }

            }
        }
        
        
        lblPrimeDisp.setText(String.valueOf(isPrime));
    
    }
    
