    private void determineFibonacci(int num)
    {
        if (num<1)
        {
           txtFibonacci.setText("");
           lblFibonacciDisp.setText("");
           return; 
        }
        
        int xn1 = 0;
        int xn2 = 1;
        int xn = 0;
        
        if(num==1)
        {
            xn = xn1;
        
        }
        else if(num==2)
        {
            xn = xn2;
        }
        else
        { 
            for(int i=3; i<=num; i++)
            {
                xn = xn1 + xn2; 
                xn1 = xn2;
                xn2 = xn;

            }
        } 
        lblFibonacciDisp.setText(String.valueOf(xn));
    }
       
